package LogicaProgramacao.src.Aula8.LMS;import java.text.DecimalFormat;import java.text.DecimalFormatSymbols;import java.util.Locale;import static java.util.Locale.setDefault;public class EX003 {    public static void main(String[] args) {        setDefault(Locale.US);        double[] entrada = new double[]{250.10, 1.0};        double saida = valorProdutoFinal(entrada);        System.out.println(saida);    }    public static double valorProdutoFinal(double[] input) {        double valorProduto = input[0];        double valorImpostos = valorImposto(input[1])/100d;        double valorFinal=-1.0d;        if(valorImpostos>0){            valorFinal = (1+valorImpostos)*valorProduto;        }        return arredondar(valorFinal);        }    public static int valorImposto(double numeroEstado){        int imposto=0;        switch ((int) numeroEstado){            case 1:                imposto=7;                break;            case 2:                imposto = 12;                break;            case 3:                imposto = 15;                break;            default:                imposto=-1;                break;        }        return imposto;    }    public static double arredondar(double numero){        DecimalFormatSymbols symbols = new DecimalFormatSymbols(Locale.US);        DecimalFormat decimalFormat = new DecimalFormat("#.####");        double numeroArredondado = Double.parseDouble(decimalFormat.format(numero));        return numeroArredondado;    }}